commits.content:
.Title{
    font-weight: bold;
    padding-left: 5px;
}
.AvatarPage{
    /* color: white;
    background-color: black; */
    display: block;
    overflow: visible;
    font-size: small;
}
    .Packages{
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
    }       
        .PackageCard{
            background-color: rgb(243, 222, 225);
            height: 50px;
            width: 230px;
            align-items: center;
        }

        .PackageBoxes{
            display: flex;
            background-color: rgb(248, 239, 241);

        }

        .PackageBox{
            margin: 3px;
            background-color: rgb(246, 252, 252);
            border: solid;
            border-color: rgb(122, 104, 0);
            border-width: 1px;
            border-radius: 10px;

        }

            .PackageLeaf{
                background-image:linear-gradient(180deg, #f0e4ca, #f5eddd);
            }
            .PackageBoxName{
                height:30px;
                line-height: 30px;
                background-color: rgb(213, 245, 161);
                background-image: linear-gradient(135deg, rgb(46, 18, 60), rgb(72, 13, 93));
                color: white;

                vertical-align: middle;
                margin: 0px;
                font-weight: bold;

            }

            .PackageContents{
                display: flex;
                flex-wrap: wrap;

            }
        .ItemBox{
            width: 220px;
            padding: 2px;
            background-color:#ffffff;
            margin: 2px;
            border: solid;
            border-color: rgb(238, 202, 202);
            border-width: 1px;
            border-radius: 10px;

        }
    .ItemCard{
        display: block;
    }


    .LeafOverall{
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;             
        background-image:linear-gradient(180deg, #e7caf0, rgb(248, 245, 245));
    }
    .LeafOverallCategory{
        margin: 5px 0px 5px 0px;

    }
        .LeafOverallCategory#avatar .Contents{

            display: flex;
            flex-direction: row;
            flex-wrap: wrap;
            border-radius: 10px;
        }
        .DesignLayer#avatar{
            display: flex;
            flex-direction: row;
            flex-wrap: wrap;    
        }
        .LeafOverallCategory#weapon .Contents{
            display: flex;
            flex-direction: row;    
            flex-wrap: wrap;;
            border-radius: 10px;
        } 
        .LeafWeaponCard { 
            display: flex;
            flex-direction: row;    
            border-radius: 10px;    
        }
        .LeafOverallCategory#instrument .Contents{
            display: flex;
            flex-direction: row;    
            flex-wrap: wrap;
            border-radius: 10px;
        } 
        .LeafCard{
            width: 214px;
            padding: 5px;
            background-color:#ffffff;
            margin: 2px;
            border: solid;
            border-color: rgb(240, 204, 204);
            border-width: 1px;
            border-radius: 10px;

        }

        .LeafCardClassName{
            font-weight:bold;
        }
.DisplayItemPrice{       
    width: 100%;
    font-weight: 'bold';
    color: rgb(150, 130, 0)   
}

.SelectedLeafId{
    margin-right: 5px;
    display: inline-block;
    vertical-align: middle;
    line-height: normal;
}
.TopButton{
    position: fixed;
    font-size: 30;
    bottom: 1rem;
    right: 1rem;
    background-color: White;
    border: solid;
    border-color: black;
    border-radius: 5px;
}
.TopButton:hover{
    background-color: rgb(196, 191, 191);
}

.ItemCard#UnfoldableBox:hover{
    background-color:rgb(252, 218, 236);
    border-radius: 10px;
}
